function createCardModel(json,majorclass,imgclass){let imgPathtrending=json.images.downsized_medium.url,gifId=json.id;const[titlefinal,userfinal]=titlecreator(json.title);let infoStorage=modelStorage(json);return card=`<div class= "${majorclass}">\n        <div class="cardoptions cardoptionshover">\n            <img class="card card-interaction1" src="Assets/icon-max-normal.svg"> \n            <img class="card card-interaction2" src="Assets/icon-download.svg"> \n            <img class="card card-interaction3 " src="Assets/icon-fav.svg"> \n            <img class="card card-interaction3-active ${gifId}" src="Assets/icon-fav-active.svg"> \n            <p class="gifInfo" style="display:none;"> ${infoStorage} </p>\n            <div class="cardtitles"> \n                <h4>${userfinal}</h4>\n                <p class="titlecurrentgif" >${titlefinal}</p>\n            </div>\n            <div class="overlay-colorselect"> </div>\n        </div>\n            <img class="${imgclass}" src="${imgPathtrending}" alt="${titlefinal}"> \n        </div>\n        `}const modelStorage=json=>infoStorage=`\n    {  "title": "${json.title}",\n        "id": "${json.id}",\n        "images":{\n            "downsized_medium":{\n                "url": "${json.images.downsized_medium.url}"\n            }\n        }\n    }`,titlecreator=title=>{if(title.includes("by")){let dividiendoeltitulo=title.split("by"),titlefinal,userfinal;return[dividiendoeltitulo[0].replace("GIF",""),dividiendoeltitulo[1].trim(0)]}{let titlefinal,userfinal;return[title.replace("GIF","")," "]}};